import React, { useState, useEffect } from 'react';
const TaskItem = ({ task, onDeleteTask, onUpdateTask }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedTask, setEditedTask] = useState(task.task);
  const [editedDescription, setEditedDescription] = useState(task.description);
  const [editedPriority, setEditedPriority] = useState(task.priority);
  const [editedDate, setEditedDate] = useState(task.finishingDate);
  const [animate, setAnimate] = useState(false);
  const handleUpdate = () => {
    onUpdateTask(task.id, {
      ...task,
      task: editedTask,
      description: editedDescription,
      priority: editedPriority,
      finishingDate: editedDate
    });
    setIsEditing(false);
  };
  const getPriorityColor = (priority) => {
    switch(priority) {
      case 'high': return '#f44336';
      case 'medium': return '#ff9800';
      case 'low': return '#4caf50';
      default: return '#757575';
    }
  };
  const getDateColor = () => {
    const today = new Date();
    const taskDate = new Date(task.finishingDate);

    if (taskDate < today) {
      return '#f44336'; // Rot für überfällige Aufgaben
    } else if (taskDate.toDateString() === today.toDateString()) {
      return '#ff9800'; // Orange für heutige Aufgaben
    }
    return '#4caf50'; // Grün für zukünftige Aufgaben
  };
  return (
    <div className={`task-item ${animate ? 'task-completed-animation' : ''}`}>
      {isEditing ? (
        <div className="task-edit-form">
          <input
            type="text"
            value={editedTask}
            onChange={(e) => setEditedTask(e.target.value)}
            className="edit-input"
            placeholder="Aufgabe"
          />
          <textarea
            value={editedDescription}
            onChange={(e) => setEditedDescription(e.target.value)}
            className="edit-input"
            placeholder="Beschreibung"
          />
          <select
            value={editedPriority}
            onChange={(e) => setEditedPriority(e.target.value)}
            className="edit-input"
          >
            <option value="low">Niedrig</option>
            <option value="medium">Mittel</option>
            <option value="high">Hoch</option>
          </select>
          <input
            type="date"
            value={editedDate}
            onChange={(e) => setEditedDate(e.target.value)}
            className="date-input"
          />
          <div className="task-actions">
            <button className="button" onClick={handleUpdate}>Speichern</button>
            <button className="button" onClick={() => setIsEditing(false)}>Abbrechen</button>
          </div>
        </div>
      ) : (
        <div className="task-view">
          <div className="task-header">
            <input
              type="checkbox"
              checked={task.completed}
              onChange={() => onUpdateTask(task.id, { ...task, completed: !task.completed })}
            />
            <div className="task-content">
              <span className="task-title" style={{ 
                color: task.completed ? '#757575' : '#212121',
                textDecoration: task.completed ? 'line-through' : 'none'
              }}>
                {task.task}
              </span>
              {task.description && (
                <p className="task-description">{task.description}</p>
              )}
            </div>
          </div>
          <div className="task-footer">
            <div className="task-meta">
              <span className="priority-badge" style={{ backgroundColor: getPriorityColor(task.priority) }}>
                {task.priority}
              </span>
              <span className="task-date" style={{ color: getDateColor() }}>
                {task.finishingDate}
              </span>
            </div>
            <div className="task-actions">
              <button className="button edit-button" onClick={() => setIsEditing(true)}>
                Bearbeiten
              </button>
              <button className="button delete-button" onClick={() => onDeleteTask(task.id)}>
                Löschen
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
export default TaskItem;
