import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import TaskPage from './TaskPage';
import '../App.css';

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:8080";

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get(`${API_URL}/tasks`);
      setTasks(response.data);
      setErrorMessage("");
    } catch (error) {
      setErrorMessage("Fehler beim Laden der Aufgaben!");
    }
  };

  const handleAddTask = async (task) => {
    try {
      const response = await axios.post(`${API_URL}/tasks`, task);
      setTasks([...tasks, response.data]);
    } catch (error) {
      setErrorMessage("Fehler beim Hinzufügen einer Aufgabe!");
    }
  };

  const handleUpdateTask = async (id, updatedTask) => {
    try {
      const response = await axios.put(`${API_URL}/tasks/${id}`, updatedTask);
      setTasks(tasks.map(task => (task.id === id ? response.data : task)));
    } catch (error) {
      setErrorMessage("Fehler beim Aktualisieren der Aufgabe!");
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`${API_URL}/tasks/${id}`);
      setTasks(tasks.filter(task => task.id !== id));
    } catch (error) {
      setErrorMessage("Fehler beim Löschen der Aufgabe!");
    }
  };

  const handleFetchTasksClick = async () => {
    await fetchTasks();
    navigate('/tasksShown');
  };

  return (
    <Router>
      <div className="App">
        <h1>Todo List</h1>
        {errorMessage && <p className="error">{errorMessage}</p>}
        <button onClick={handleFetchTasksClick}>Fetch Tasks</button>
        <button onClick={() => navigate('/tasks')}>Add Tasks</button>
        <Routes>
          <Route path="/tasks" element={<TaskPage tasks={tasks} onAddTask={handleAddTask} onUpdateTask={handleUpdateTask} onDeleteTask={handleDeleteTask} />} />
          <Route path="/tasksShown" element={<TaskPage tasks={tasks} onAddTask={handleAddTask} onUpdateTask={handleUpdateTask} onDeleteTask={handleDeleteTask} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
