import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Switch, useHistory } from 'react-router-dom';
import TaskForm from './TaskForm';
import TaskPage from './TaskPage';
import TasksShownPage from './TasksShownPage';
import '../App.css';

const App = () => {
  const [tasks, setTasks] = useState([]);
  const history = useHistory();

  const fetchTasks = useCallback(async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/tasks`);
      setTasks(response.data);
      if (history.location.pathname !== '/tasksShown') {
        history.push('/tasksShown'); // Navigate to the results page after fetching tasks
      }
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  }, [history]);

  useEffect(() => {
    fetchTasks();
  }, [fetchTasks]);

  const handleAddTask = async (task) => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/tasks`, task);
      setTasks([...tasks, response.data]);
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const handleUpdateTask = async (id, updatedTask) => {
    try {
      const response = await axios.put(`${process.env.REACT_APP_API_URL}/tasks/${id}`, updatedTask);
      setTasks(tasks.map(task => (task.id === id ? response.data : task)));
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/tasks/${id}`);
      setTasks(tasks.filter(task => task.id !== id));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleFetchTasksClick = async () => {
    await fetchTasks();
    history.push('/tasksShown');
  };

  return (
    <Router>
      <div className="App">
        <h1>Todo List</h1>
        <button onClick={handleFetchTasksClick}>Fetch Tasks</button>
        <TaskForm onAddTask={handleAddTask} />
        <Switch>
          <Route path="/tasks">
            <TaskPage tasks={tasks} onDeleteTask={handleDeleteTask} onUpdateTask={handleUpdateTask} />
          </Route>
          <Route path="/tasksShown">
            <TasksShownPage tasks={tasks} onDeleteTask={handleDeleteTask} onUpdateTask={handleUpdateTask} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
