import React, { useState } from 'react';
const TaskForm = ({ onAddTask }) => {
  const [task, setTask] = useState('');
  const [description, setDescription] = useState('');
  const [priority, setPriority] = useState('medium');
  const [finishingDate, setFinishingDate] = useState('');
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!task.trim() || !finishingDate) {
      alert('Bitte füllen Sie alle Pflichtfelder aus');
      return;
    }
    try {
      const newTask = {
        task: task.trim(),
        description: description.trim(),
        priority,
        finishingDate,
        completed: false
      };
      await onAddTask(newTask);
      setTask('');
      setDescription('');
      setPriority('medium');
      setFinishingDate('');
    } catch (error) {
      console.error('Fehler beim Hinzufügen der Aufgabe:', error);
      alert('Fehler beim Hinzufügen der Aufgabe');
    }
  };
  return (
    <form onSubmit={handleSubmit} className="task-form container">
      <div className="form-group">
        <input
          type="text"
          placeholder="Neue Aufgabe eingeben"
          value={task}
          onChange={(e) => setTask(e.target.value)}
          className="form-input"
          required
        />
        <textarea
          placeholder="Beschreibung (optional)"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="form-input"
        />
        <select
          value={priority}
          onChange={(e) => setPriority(e.target.value)}
          className="form-input"
        >
          <option value="low">Niedrig</option>
          <option value="medium">Mittel</option>
          <option value="high">Hoch</option>
        </select>
        <input
          type="date"
          value={finishingDate}
          onChange={(e) => setFinishingDate(e.target.value)}
          className="form-input"
          required
        />
        <button type="submit" className="button">
          Aufgabe hinzufügen
        </button>
      </div>
    </form>
  );
};
export default TaskForm;
