import React, { useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import TaskPage from './TaskPage';
import TaskForm from './TaskForm';
const App = () => {
  const [tasks, setTasks] = useState([
    // Beispielhafte Tasks zur Demo
    { id: 1, text: 'Erste Aufgabe', finishingDate: '2023-12-31', completed: false },
    { id: 2, text: 'Zweite Aufgabe', finishingDate: '2024-01-15', completed: false }
  ]);
  // Update-Handler mit Debugging-Ausgaben
const handleUpdateTask = async (taskId, updatedTask) => {
    console.log('handleUpdateTask aufgerufen:', {taskId, updatedTask});
    try {
      const response = await fetch(`/api/tasks/${taskId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(updatedTask)
      });

      if (!response.ok) {
        console.error('Update fehlgeschlagen:', response.statusText);
        throw new Error(`Update fehlgeschlagen: ${response.status}`);
      }
      const data = await response.json();
      console.log('Update erfolgreich. Antwort vom Backend:', data);

      setTasks((prevTasks) =>
        prevTasks.map((task) => (task.id === taskId ? data : task))
      );
    } catch (error) {
      console.error('Fehler beim Aktualisieren des Tasks:', error);
      throw error;
    }
  };
  // Beispielhafter Add-Handler (hier Dummy-Code)
  const handleAddTask = async (newTask) => {
    console.log('handleAddTask aufgerufen:', newTask);
    try {
      const response = await fetch('/api/tasks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newTask)
      });
      if (!response.ok) {
        console.error('Hinzufügen fehlgeschlagen:', response.statusText);
        throw new Error(`Hinzufügen fehlgeschlagen: ${response.status}`);
      }
      const createdTask = await response.json();
      setTasks((prevTasks) => [...prevTasks, createdTask]);
    } catch (error) {
      console.error('Fehler beim Hinzufügen der Task:', error);
    }
  };
  return (
    <Router>
      <div className="App">
        <h1>Todo List</h1>

        {/* TaskForm zum Erstellen neuer Tasks */}
        <TaskForm onAddTask={handleAddTask} />
        <Switch>
          <Route path="/tasks">
            <TaskPage
              tasks={tasks}
              onUpdateTask={handleUpdateTask}
              // Bei Bedarf weitere Handler wie onDeleteTask hier einfügen
            />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};
export default App;
