import React, { useState, useEffect } from 'react';
const TaskItem = ({ task, onDeleteTask, onUpdateTask }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedTask, setEditedTask] = useState(task.task);
  const [editedDate, setEditedDate] = useState(task.finishingDate);
  const [animate, setAnimate] = useState(false);
  useEffect(() => {
    if (task.completed) {
      setAnimate(true);
      const timer = setTimeout(() => setAnimate(false), 500);
      return () => clearTimeout(timer);
    }
  }, [task.completed]);
  const getDateColor = () => {
    if (task.completed) return '#757575';

    const today = new Date();
    const dueDate = new Date(task.finishingDate);
    const diffDays = Math.ceil((dueDate - today) / (1000 * 60 * 60 * 24));
    if (diffDays < 0) return '#f44336'; // Überfällig - Rot
    if (diffDays <= 2) return '#ff9800'; // Bald fällig - Orange
    return '#4caf50'; // Genügend Zeit - Grün
  };
  const handleUpdate = () => {
    onUpdateTask(task.id, {
      ...task,
      task: editedTask,
      finishingDate: editedDate
    });
    setIsEditing(false);
  };
  return (
    <div className={`task-item ${animate ? 'task-completed-animation' : ''}`}>
      {isEditing ? (
        <div className="task-edit-form">
          <input
            type="text"
            value={editedTask}
            onChange={(e) => setEditedTask(e.target.value)}
            className="edit-input"
          />
          <input
            type="date"
            value={editedDate}
            onChange={(e) => setEditedDate(e.target.value)}
            className="date-input"
          />
          <div className="task-actions">
            <button className="button" onClick={handleUpdate}>Speichern</button>
            <button className="button" onClick={() => setIsEditing(false)}>Abbrechen</button>
          </div>
        </div>
      ) : (
        <>
          <input
            type="checkbox"
            checked={task.completed}
            onChange={() => onUpdateTask(task.id, { ...task, completed: !task.completed })}
          />
          <div className="task-content">
            <span style={{ 
              textDecoration: task.completed ? 'line-through' : 'none',
              color: task.completed ? '#757575' : '#212121'
            }}>
              {task.task}
            </span>
            <div className="task-date" style={{ color: getDateColor() }}>
              {task.finishingDate}
            </div>
          </div>
          <div className="task-actions">
            <button className="button edit-button" onClick={() => setIsEditing(true)}>
              Bearbeiten
            </button>
            <button className="button delete-button" onClick={() => onDeleteTask(task.id)}>
              Löschen
            </button>
          </div>
        </>
      )}
    </div>
  );
};
export default TaskItem;
