import React, { useState, useEffect } from 'react';

const TaskForm = ({ onAddTask, initialTask }) => {
  const [task, setTask] = useState('');
  const [finishingDate, setFinishingDate] = useState('');

  useEffect(() => {
    if (initialTask) {
      setTask(initialTask.task);
      setFinishingDate(initialTask.finishingDate);
    }
  }, [initialTask]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const updatedTask = { ...initialTask, task, finishingDate, completed: false };

if (initialTask) {
      onAddTask(updatedTask);
    } else {
      onAddTask(updatedTask);
    }
    setTask('');
    setFinishingDate('');

    setTask('');
    setFinishingDate('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Task"
        value={task}
        onChange={(e) => setTask(e.target.value)}
        required
      />
      <input
        type="date"
        value={finishingDate}
        onChange={(e) => setFinishingDate(e.target.value)}
        required
      />
      <button type="submit">{initialTask ? 'Update Task' : 'Add Task'}</button>
    </form>
  );
};

export default TaskForm;
