import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';

const App = () => {
  const [tasks, setTasks] = useState([]);
  const navigate = useNavigate(); // useNavigate statt useHistory

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleFetchTasksClick = async () => {
    await fetchTasks();
    navigate('/tasksShown'); // useNavigate statt history.push
  };

  return (
    <Router>
      <div className="App">
        <h1>Todo List</h1>
        <button onClick={handleFetchTasksClick}>Fetch Tasks</button>
        <button onClick={() => navigate('/tasks')}>Add Tasks</button>
        <Routes>
          <Route path="/tasks" element={<TaskPage tasks={tasks} />} />
          <Route path="/tasksShown" element={<TaskPage tasks={tasks} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
