import React, { useState } from 'react';
import TaskItem from './TaskItem';

const TaskForm = ({ onAddTask }) => {
  const [task, setTask] = useState('');
  const [finishingDate, setFinishingDate] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddTask({ task, finishingDate, completed: false });
    setTask('');
    setFinishingDate('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Task"
        value={task}
        onChange={(e) => setTask(e.target.value)}
        required
      />
      <input
        type="date"
        value={finishingDate}
        onChange={(e) => setFinishingDate(e.target.value)}
        required
      />
      <button type="submit">Add Task</button>
    </form>
  );
};

const TaskList = ({ tasks, onUpdateTask, onDeleteTask, onAddTask }) => {
  const [newTask, setNewTask] = useState('');

  const handleAddTask = (e) => {
    e.preventDefault();
    if (newTask.trim()) {
      onAddTask(newTask);
      setNewTask('');
    }
  };

  return (
    <div>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            {task.name}
            {/* Add buttons or other elements to update and delete tasks */}
          </li>
        ))}
      </ul>
      <form onSubmit={handleAddTask}>
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add a new task"
        />
        <button type="submit">Add Task</button>
      </form>
    </div>
  );
};

export { TaskForm, TaskList };
