import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Route, Switch, useHistory } from 'react-router-dom';
import TaskForm from './TaskForm';
import TaskPage from './TaskPage';
import '../App.css';

const App = () => {
  const [tasks, setTasks] = useState([]);
  const history = useHistory();

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/tasks`);
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleAddTask = async (task) => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/tasks`, task);
      setTasks([...tasks, response.data]);
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const handleUpdateTask = async (id, updatedTask) => {
    try {
      const response = await axios.put(`${process.env.REACT_APP_API_URL}/tasks/${id}`, updatedTask);
      setTasks(tasks.map(task => (task.id === id ? response.data : task)));
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/tasks/${id}`);
      setTasks(tasks.filter(task => task.id !== id));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleFetchTasksClick = () => {
    fetchTasks();
    history.push('/tasks');
  };

  return (
    <div className="App">
      <h1>Todo List</h1>
      <button onClick={handleFetchTasksClick}>Fetch Tasks</button>
      <TaskForm onAddTask={handleAddTask} />
      <Switch>
        <Route path="/tasks">
          <TaskPage tasks={tasks} onUpdateTask={handleUpdateTask} onDeleteTask={handleDeleteTask} />
        </Route>
      </Switch>
    </div>
  );
};

export default App;
