import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Switch, useHistory } from 'react-router-dom';
import TaskForm from './TaskForm';
import TaskPage from './TaskPage';
import TasksShownPage from './TasksShownPage';
import '../App.css';
const App = () => {
const [tasks, setTasks] = useState([]);
const [tasksLoaded, setTasksLoaded] = useState(false);
const [loading, setLoading] = useState(false);
const history = useHistory();
const fetchTasks = useCallback(async () => {
  setLoading(true);
  try {
    const response = await axios.get(`${process.env.REACT_APP_API_URL}/tasks`);
    setTasks(response.data);
    setTasksLoaded(true);
  } catch (error) {
    console.error('Error fetching tasks:', error);
  } finally {
    setLoading(false);
  }
}, []);
  const handleAddTask = async (task) => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/tasks`, task);
      setTasks(prevTasks => [...prevTasks, response.data]);
    } catch (error) {
console.error('Error adding task:', error);
    }
  };
  const handleUpdateTask = async (id, updatedTask) => {
    try {
      const response = await axios.put(`${process.env.REACT_APP_API_URL}/tasks/${id}`, updatedTask);
      setTasks(prevTasks => prevTasks.map(task => (task.id === id ? response.data : task)));
    } catch (error) {
console.error('Error updating task:', error);
    }
  };
  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/tasks/${id}`);
      setTasks(prevTasks => prevTasks.filter(task => task.id !== id));
    } catch (error) {
console.error('Error deleting task:', error);
    }
  };
  return (
    <div className="App">
      <h1>Todo List</h1>
      <Switch>
        <Route exact path="/">
<button onClick={() => {
            fetchTasks();
            history.push('/tasksShown');
          }} className="button">
            Fetch Tasks
          </button>
<TaskPage 
            tasks={tasks} 
            onAddTask={handleAddTask}
            tasksLoaded={tasksLoaded}
          />
        </Route>
        <Route path="/tasksShown">
          <TasksShownPage 
            tasks={tasks} 
            onDeleteTask={handleDeleteTask} 
            onUpdateTask={handleUpdateTask} 
          />
        </Route>
      </Switch>
    </div>
  );
};
export default App;
